import './App.css';
import React from 'react'
import { useState } from 'react'
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'
import axios from 'axios'

function App() {

  const [crypto, setCrypto] = useState('')
  const [pic, setPic] = useState("")
  const [name, setName] = useState("")
  const [symb, setSymb] = useState("")
  const [lynk, setLynk] = useState("")

  const data_fetch = () => {
    const url = 'https://api.coingecko.com/api/v3/coins/' + crypto;
    axios.get(url)
      .then(result => {
        console.log(result.data)
        const resData = result.data
        setPic(resData.image.large)
        setName(resData.name)
        setSymb(resData.symbol)
        setLynk(resData.links.blockchain_site + ".0")

      })
      .catch(err => console.log(err))
  }

  return (
    <React.Fragment>
      <div className="App p-4 container-fluid" >
        <h1 className="title">Cryptocurrency Search</h1>
      </div>
      <div>
        <div className="col-md-12 d-flex justify-content-center p-5 " >
          <input type="text" value={crypto} onChange={(e) => setCrypto(e.target.value)} placeholder="Input text" className="w-25 form-control" required />
          <button onClick={data_fetch} className='btn btn-secondary '>Search</button>
        </div>
      </div>
      <div className='row'>
        <div className='col-md-4 p-5  text-center '>
          <img src={pic} width="150" className="py-3" /><br />
          <h1 style={{ backgroundColor: "blue" }}>{name}</h1><br />
          <h2 style={{ backgroundColor: "red" }}>{symb}</h2><br />
          <h3><a href={lynk}>{lynk}</a></h3><br /><br />
          {/* <h2>{ }</h2><br />
          <h2>{ }</h2> */}


        </div>
        <div className='col-md-8'>

        </div>
      </div>
    </React.Fragment>
  );
}

export default App;
